import React from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import GroupedBarChart from './GroupedBarChart';
import {
  Box,
  Button,
  Card,
  CardContent,
  CardHeader,
  Divider,
  useTheme,
  makeStyles,
  colors
} from '@material-ui/core';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ArrowRightIcon from '@material-ui/icons/ArrowRight';
import CircularProgress from '@material-ui/core/CircularProgress';
import Select from '@material-ui/core/Select';


const useStyles = makeStyles(() => ({
  root: {}
}));
const apiUrl = 'https://visualizing-healthcare-data.wm.r.appspot.com/diagnosis_dist/gender/';

const DiagnosesBarChart = ({ className, ...rest }) => {
  const classes = useStyles();
  const theme = useTheme();

  const [loading, setLoading] = React.useState(true);
  const [data, setData] = React.useState([]);


  const options = [{ value: 'gender', label: 'Gender' },
  { value: 'religion', label: 'Religion' },
  { value: 'ethnicity', label: 'Ethnicity' },
  { value: 'maritalstatus', label: 'Marital Status' }
  ];



  React.useEffect(() => {
    fetch(apiUrl)
      .then((response) => response.json())
      .then(result => {
        setData(result);
        setLoading(false);
      })

  }, [])
  console.log(data)




  return (
    <Card
      className={clsx(classes.root, className)}
      {...rest}
    >
      <CardHeader
        action={(
          <Select
            endIcon={<ArrowDropDownIcon />}
            value={options}
            onChange={handleChange}
                      >
          </Select>
        )}
        title="ICU Diagnoses discovered by Gender"
      />
      <Divider />
      <CardContent>
        <Box
          height={400}
          position="relative"
        >
          {/* <Bar
            data={data}
            options={options}
          /> */}
          {!loading ? <GroupedBarChart data={data} size={[500, 400]} /> : <CircularProgress />}
        </Box>
      </CardContent>
      <Divider />
      <Box
        display="flex"
        justifyContent="flex-end"
        p={2}
      >
        {/* <Button
          color="primary"
          endIcon={<ArrowRightIcon />}
          size="small"
          variant="text"
        >
          Overview
        </Button> */}
      </Box>
    </Card>
  );
};

DiagnosesBarChart.propTypes = {
  className: PropTypes.string
};

export default DiagnosesBarChart;
